    $('.action-panel-item select').on('change', function() { 
        var offset = $('#people-list').find('div.item-list').length;
        
        $.ajax({
            url: ajaxurl,
            method: 'GET',
            data: {
                action: 'load-more-people',
                //offset: offset,
                role: $('.roles_filter').val(),
                location: $('.location_filter').val(),
                division: $('.division_filter').val(),
            },
            dataType: 'html',
            beforeSend: function() {
            },
            success: function(response) {
                $('#people-list').empty();
                $('#people-list').append(response);
                if(response == "") {
                    $(".load-more-people-btn").hide();
                    $('.no-record').show();
                } else {
                    $('.load-more-people-btn').removeClass('active');
                    $(".load-more-people-btn").show();
                    $('.no-record').hide();
                }
                
                if($('.roles_filter').val()){
                    $('.roles_filter').parent().addClass('active');
                } else {
                    $('.roles_filter').parent().removeClass('active');
                }
                
                if($('.location_filter').val()){
                    $('.location_filter').parent().addClass('active');
                } else {
                    $('.location_filter').parent().removeClass('active');
                }
                
                if($('.division_filter').val()){
                    $('.division_filter').parent().addClass('active');
                } else {
                    $('.division_filter').parent().removeClass('active');
                }
            },
            complete: function() {
            }
        });
    });
    
    $(".btn-download").click(function(e) {
        e.preventDefault();
        $(".pop-up-wrap").removeClass("pop-up-hidden");
        $("body").css("overflow", "hidden");
        EventClick("modal")
    });
    
    $(".pop-up .icon-close").click(function() {
        popupClose("modal")
    });

    function EventClick(data) {
        $(document).mouseup(function(e) {
            if (data == "modal" && $(".pop-up-content-inner").has(e.target).length === 0) {
                e.preventDefault();
                if (data == "modal") {
                    $(".pop-up-wrap").addClass("pop-up-hidden");
                    $("body").css("overflow", "auto");
                }
            }
        })
    };
    EventClick("modal");

    function popupClose(data) {
        if (data == "modal") {
            $(".pop-up-wrap").addClass("pop-up-hidden");
            $("body").css("overflow", "auto")
        }
    };
